    $marketcloud.orders.update(scope.order.id, {
      billing_address: scope.order.billing_address,
      shipping_address: scope.order.shipping_address
    }).then(function () {
      notie.alert(1, 'Order successfully updated', 1.5);
      $('#editAddressModal').modal('hide');
