  scope.updateVariantsConfiguration = function () {
    // Transforming addedVariants2
    scope.computedVariants = [];
    scope.addedVariants = {};
    scope.addedVariants2.forEach(function (variant) {
      scope.addedVariants[variant.name] = variant.values.map(function (i) {
