          var element = angular.element('[ng-model="' + invalidPropertyName + '"]').parent();

          $validation.showErrorMessage(validation, '[ng-model="' + invalidPropertyName + '"]');
        } else notie.alert(3, 'Update failed', 1.5);
      });
    };
  }
})();

