angular.module('DataDashboard').controller('ListVariantsController', ['$scope', '$marketcloud', '$routeParams', '$http', 'resolvedProduct', function (scope, $marketcloud, params, $http, resolvedProduct) {

  scope.product = resolvedProduct.data.data;

  // These cannot be touched
  scope.variantDefiningProperties = {};

  scope.deleteVariant = function (variant) {
    return $http({
      method: 'DELETE',
      url: API_BASE_URL + '/products/' + scope.product.id + '/variants/' + variant.id,
      headers: {
        Authorization: window.public_key + ':' + window.token
      }
    }).then(function (response) {
      notie.alert(1, 'Variant deleted', 1.5);
      scope.product = response.data.data;
    }).catch(function (response) {
      notie.alert(3, 'An error has occurred, please try again.', 1.5);
    });
  };

  scope.clone = function (variant) {
    var variantToSave = angular.copy(variant);
    // Removing useless stuff in the variant
    delete variantToSave['id'];

    return $http({
      method: 'POST',
      url: API_BASE_URL + '/products/' + scope.product.id + '/variants',
      data: variantToSave,
      headers: {
        Authorization: window.public_key + ':' + window.token
      }
    }).then(function (response) {
      notie.alert(1, "Variant successfully cloned", 2);
    }).catch(function (response) {
      notie.alert(2, "An error has occurred, please try again", 2);
    });
  };

  scope.filterVariantProps = function (o) {
    var p = {};
    for (var k in o) {
      // if its not an inherited property, then it's a variant property name
      if (scope.product.variantsDefinition.hasOwnProperty(k)) {
        p[k] = k + ': ' + String(o[k]);
      }
    }
    return p;

    // return o
  };

  scope.getVariantStyle = function (i) {
    var r = [{
      'color': '#c0392b'
    }, {
      'color': '#27ae60'
    }, {
      'color': '#2c3e50'
    }, {
      'color': '#d35400'
    }];
    return r[i % r.length];
  };
}]);

'use strict';

var app = angular.module('DataDashboard');

app.controller('WebhooksController', ['$scope', '$http', '$location', 'webhooks', function (scope, http, location, webhooks) {

	scope.mode = null;

	scope.webhooks = [{
		event: 'products.create',
		method: 'POST',
		url: 'mybackend.herokuapp.com/newproduct',
		headers: {}
	}];

	scope.webhooks = webhooks.data.data;

	scope.events = ['products.create', 'products.delete', 'products.update', 'brands.create', 'brands.delete', 'brands.update', 'categories.create', 'categories.delete', 'categories.update', 'orders.create', 'orders.delete', 'orders.update', 'carts.create', 'carts.delete', 'carts.update', 'payments.create', 'payments.delete', 'payments.update', 'shippings.create', 'shippings.delete', 'shippings.update'];
	scope.load = function () {
		http({
			method: 'GET',
			url: '/applications/' + window.current_application.id + '/webhooks'
		}).then(function (response) {
			scope.webhooks = response.data.data;
		}).catch(function (response) {
			notie.alert(2, 'An error has occurred while reloading webhooks.', 1);
		});
	};

	scope.edit = function (hook) {
		scope.newWebhook = hook;
		scope.showUpdateModal();
	};
	scope.showUpdateModal = function () {
		scope.mode = 'update';
		$('#newWebhookModal').modal('show');
	};

	scope.showCreateModal = function () {
		scope.mode = 'create';
		$('#newWebhookModal').modal('show');
	};

	scope.saveWebhook = function () {
		http({
			method: 'POST',
			url: '/applications/' + window.current_application.id + '/webhooks',
			data: scope.newWebhook
		}).then(function (response) {
			notie.alert(1, 'Webhook correctly created', 1);
			scope.newWebhook = {};
			$('#newWebhookModal').modal('hide');
			scope.load();
		}).catch(function (response) {
			notie.alert(3, 'An error has occurred, webhook not created', 1);
			scope.newWebhook = {};
		});
	};

	scope.updateWebhook = function () {
		http({
			method: 'PUT',
			url: '/applications/' + window.current_application.id + '/webhooks/' + scope.newWebhook.id,
			data: scope.newWebhook
		}).then(function (response) {
			notie.alert(1, 'Webhook correctly updated', 1);
			scope.newWebhook = {};
		}).catch(function (response) {
			notie.alert(3, 'An error has occurred, webhook not updated', 1);
			scope.newWebhook = {};
		});
	};

	scope.delete = function (hook) {
		http({
			method: 'DELETE',
			url: '/applications/' + window.current_application.id + '/webhooks/' + hook.id
		}).then(function (response) {
			notie.alert(1, 'Webhook correctly deleted', 1);
			scope.load();
		}).catch(function (response) {
			notie.alert(3, 'An error has occurred, webhook not deleted', 1);
		});
	};
}]);

