'use strict';

var app = angular.module('DataDashboard');
app.directive('focusOnShow', function ($timeout) {
	return {
		restrict: 'A',
		link: function link($scope, $element, $attr) {
			if ($attr.ngShow) {
				$scope.$watch($attr.ngShow, function (newValue) {
					if (newValue) {
						$timeout(function () {
							$element[0].focus();
						}, 0);
					}
				});
			}
			if ($attr.ngHide) {
				$scope.$watch($attr.ngHide, function (newValue) {
					if (!newValue) {
						$timeout(function () {
							$element[0].focus();
						}, 0);
					}
				});
			}
		}
	};
});

